import React, {useState} from 'react'
import { useSelector } from 'react-redux';
// import {CreateTimetable} from '../../../reducer/crmReducer/action'
import { useHistory } from 'react-router-dom';
import {useDispatch } from 'react-redux';
import Classes from './classes';
import Teachers from './teachers';
import Student from './students';
import Timetable from './timetable'
import {useSpring, animated} from 'react-spring'

const Create = () => {
  const [classes,setClasses ] = useState(false)
  const [teacher,setTeachers ] = useState(false)
  const [student,setStudent ] = useState(false)

  const  classFunction = ( param ) => {
    if(param === false){
      setClasses(true)
    }else{
      setClasses(false)
    }
    console.log(classes)
  }

  const animationClass = useSpring({
    to:[{
      height:classes? "50%" : "0%"
    }],
    from:{
      height: "0%"
    },
    config: {
      duration: 600
    }
  })


  const animationTeacher = useSpring({
    to:[{
      height:classes? "50%" : "0%"
    }],
    from:{
      height: "0%"
    },
    config: {
      duration: 600
    }
  })

  const animationStudent = useSpring({
    to:[{
      height:classes? "50%" : "0%"
    }],
    from:{
      height: "0%"
    },
    config: {
      duration: 600
    }
  })

  return (
    <div className="container create-page">
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-4">
            <div className="create-box" onClick={() =>{classFunction(classes) }} >
              <h3>Create Classes</h3> 
            </div>
          </div>
          <div className="col-md-4">
            <div className="create-box">
              <h3>Create Teachers</h3>
            </div>
          </div>
          <div className="col-md-4">
            <div className="create-box">
              <h3>Create Student</h3>
            </div>
          </div>
          <div className="col-md-12">
            <animated.div style={animationClass} className="classes">
              <Classes />
            </animated.div>
            <Timetable />
            {/* <Teachers />
            <Student /> */}
          </div>
        </div>
      </div>
    </div>

  )
}

export default Create
