import React, {useState,useEffect} from 'react'
import { useSelector } from 'react-redux';
import {GetTimetable} from '../../../../reducer/crmRedux/action'
import { useHistory } from 'react-router-dom';
import {useDispatch } from 'react-redux';


const Create = () => {
  const [hours, setHours] = useState('')

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(GetTimetable())
  }, [])

  let state = useSelector((state) => state);
  // console.log(state)
  const [newTimetable, setNewtimetable] = useState({
    classesId:'',
    classesName:'',
    studentsId:'',
    studentsName:'',
    teacherId:'',
    teacherName:'',
    date:''
  })


  state.classReducer.map((Class) =>{
    const {name, _id} = Class
    if(_id === newTimetable.classesId){
        newTimetable.classesName = name
      console.log(newTimetable)
    }
  })

  state.studentReducer.map((student) =>{
    const {firstName, _id} = student
    if(_id === newTimetable.studentsId){
      newTimetable.studentsName = firstName
    console.log(newTimetable)
    }
  })

  state.teacherReducer.map((student) =>{
    const {name, _id} = student
    if(_id === newTimetable.teacherId){
      newTimetable.teacherName = name
    console.log(newTimetable)
    }
  })

  const history = useHistory();

  const dt = new Date();

useEffect(() => {
  setHours( dt.getHours(10) )
}, [])

console.log( new Date(0, 0, 0, 10,0) )



  return (
    <div className="create-data">
      <p>create teacher</p>
      <form action="">
        {/* select class */}
        <select
          onChange={(e) =>{
            setNewtimetable({
              ...newTimetable, classesId: e.target.value
            })
          }} 
          name="class" id="cars">
          <option  value="select">select</option>
          {
            state.classReducer.map((clas, index) =>{
              const {name, _id} = clas

              return(
                <option key={index} value={_id}>{name}</option>
              )
            })
          }
        </select>
          {/* select student */}
        <select
        onChange={(e) =>{
          setNewtimetable({
            ...newTimetable, studentsId: e.target.value
          })
        }}
        name="students" id="cars">
          <option  value="select">select</option>
          {
            state.studentReducer.map((student, index) =>{
              const {firstName, _id} = student

              return(
                <option key={index} value={_id}>{firstName}</option>
              )
            })
          }
        </select>
        {/* select teacher  */}
        <select 
          onChange={(e) =>{
            setNewtimetable({
              ...newTimetable, teacherId: e.target.value
            })
          }}
        name="students" id="cars">
          <option  value="select">select</option>
          {
            state.teacherReducer.map((teacher, index) =>{
              const {name, _id} = teacher

              return(
                <option key={index} value={_id}>{name}</option>
              )
            })
          }
        </select>
      </form>


      <div className="data">
        <ul>
          {
            state.timetableReducer.map((timetable, index) =>{
              const {
                student_Name,
                student_Id,
                teacher_Name,
                teacher_Id,
                class_Name,
                class_Id,
                date
              } =timetable

              return(
                <div key={index}>
                  <button
                    // onClick={()=>{
                    //   setId({id: _id})
                    // }}
                  >
                    update
                  </button>
                  {/* <li   value={_id} >
                  <span>status: {
                      status === 1? ( <span>active</span> ) :( <span>inactive</span> )
                    } 
                  </span>
                  </li> */}
                  <li>
                    <span> teacher: {teacher_Name} </span>
                  </li>
                  <li>
                    <span> student: {student_Name} </span>
                  </li>
                  <li>
                    <span> class: {class_Name} </span>
                  </li>
                  <li>
                    <span> date: 10 20 2022 </span>
                  </li>
                </div>

              )
            })
          }
        </ul>
      </div>

    </div>

  )
}

export default Create
