import React, {useState} from 'react'
import { useSelector } from 'react-redux';
// import {CreateTimetable} from '../../../reducer/crmReducer/action'
import { useHistory } from 'react-router-dom';
import {useDispatch } from 'react-redux';
import Classes from './classes';
import Teachers from './teachers';
import Student from './students';
import Timetable from './timetable'
import {useSpring, animated} from 'react-spring'

const Create = () => {
  const [classes,setClasses ] = useState(false)


  const  classFunction = () => {
    if(classes){
      setClasses(false)
    }else{
      setClasses(true)
    }
    console.log(classes)
  }

  const ClassesAnimation = useSpring({
    to:[{
      height:classes? "50%" : "0%"
    }],
    from:{
      height: "0%"
    },
    config: {
      duration: 1500
    }
  })


  return (
    <div className="container create-page">
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-4">
            <div className="create-box" onClick={() =>{classFunction() }} >
              <h3>Create Classes</h3> 
            </div>
          </div>
          <div className="col-md-4">
            <div className="create-box">
              <h3>Create Teachers</h3>
            </div>
          </div>
          <div className="col-md-4">
            <div className="create-box">
              <h3>Create Student</h3>
            </div>
          </div>
          <div className="col-md-12">
            <animated.div style={Classes} className="classes">
              <Classes />
            </animated.div>
            <Timetable />
            {/* <Teachers />
            <Student /> */}
          </div>
        </div>
      </div>
    </div>

  )
}

export default Create
