import React, {useEffect, useState} from 'react'
import { useHistory } from 'react-router-dom';
import { useSelector,useDispatch } from 'react-redux';
import { CreateStudent, UpdateStudent,GetStudent} from '../../../../reducer/crmRedux/action';

const Classes = () => {
  const [data, setData] = useState({
    firstName:"",
    secondName:"",
    age:0,
    status:0
  })
  const [id, setId] = useState({})
  const dispatch = useDispatch();
  const state = useSelector((state) => state.studentReducer);
  // console.log(data)
  // get student
  useEffect(() => {
    dispatch(GetStudent())
  }, [])


// select update student
  useEffect(() => {
    state.map((classes) =>{
      const {_id} = classes
      if(_id === id.id){
        setData(classes)
      }
    })
  }, [id])


  // create and update student
  const createClass = (e) =>{
    if(id.id){
      dispatch(UpdateStudent(id.id,data))
      setData({
        firstName:"",
        secondName:"",
        age:0,
      })
    }else{
      dispatch(CreateStudent(data))
      setData({
        firstName:"",
        secondName:"",
        age:0,
      })
    }
  }


  return (
    <div className='create-data'>
      <p>create Student</p>
      <form action="">
        <label htmlFor="name">
          firstName
          <input type="name" id="name"
          value={data.firstName}
            onChange={(e) =>{
              setData({
                ...data, firstName: e.target.value
              })
            }}
          />
        </label>
        <label htmlFor="secondName">
          secondName
          <input type="secondName" id="secondName" 
            value={data.secondName}
            onChange={(e) =>{
              setData({
                ...data, secondName:e.target.value
              })
            }}
          />
        </label>
        <label htmlFor="number">
          age
          <input type="number" id="password"
            value={data.age}
            onChange={(e) =>{
              setData({
                ...data, age:e.target.value
              })
            }}
              />
        </label>
        <label htmlFor="status">
          status
          <select name="" id="status"
            onChange={(e)=>{
              setData({
                ...data, status: parseInt(e.target.value) 
              })
            }}
          >
            <option >select status</option>
            <option value="0">inactive</option>
            <option value="1">active</option>
          </select>
        </label>

      </form>
      <button
          onClick={() =>{
            createClass()
          }}
        >
          {
            id.id ? (
              <span>update</span> 
            ):(
              <span>create</span> 
            )

          }

      </button>

