import React, {useEffect,useState} from 'react'
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import {GetTeacherTable,CreateComment} from '../../../reducer/crmRedux/action'


const Teacher = () => {
  const [data, setData] = useState({
    class_Comment:'',
    complete:false
  })


  const state = useSelector((state) => state.teachertable)
  const profile = JSON.parse(localStorage.getItem('profile'));
  const dispatch = useDispatch();
  console.log(state)
  useEffect(() => {
    dispatch(GetTeacherTable(profile.superAdmin._id))
  }, [])


  const createComment = () =>{
    console.log(true)
  }
  return (
    <>
      <div className="container main-teacher-page ">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12">
              <div className="create-admin  create-student">
              <p>create Student</p>
                <form action="">
                  <label htmlFor="name">
                    <b>firstName :</b>
                    <input type="name" id="name"
                    value={data.firstName}
                      onChange={(e) =>{
                        setData({
                          ...data, firstName: e.target.value
                        })
                      }}
                    />
                  </label>
                  <label htmlFor="secondName">
                      <b>secondName :</b>
                    <input type="secondName" id="secondName" 
                      value={data.secondName}
                      onChange={(e) =>{
                        setData({
                          ...data, secondName:e.target.value
                        })
                      }}
                    />
                  </label>
                  <label htmlFor="number">
                      <b>age :</b>
                    <input type="number" id="password"
                      value={data.age}
                      onChange={(e) =>{
                        setData({
                          ...data, age:e.target.value
                        })
                      }}
                        />
                  </label>
                  <label htmlFor="status">
                      <b>status</b>
                    <select name="" id="status"
                      onChange={(e)=>{
                        setData({
                          ...data, status: parseInt(e.target.value) 
                        })
                      }}
                    >
                      <option >select status</option>
                      <option value="0">inactive</option>
                      <option value="1">active</option>
                    </select>
                  </label>
                </form>
                  <button onClick={() =>{createComment()}}>
                    create
                  </button>
              </div>
            </div>
            {
              state.map((timetable,index) =>{
                const {
                  student_Name,
                  teacher_Name,
                  class_Name,
                  date,
                  _id
                } = timetable
                // console.log(time)
                  const time = new Date(date)
                  const minute = time.getMinutes()
                  const hour = time.getHours()
                  const day = time.getDay()
                  const month = time.getMonth()
                  const year = time.getFullYear()

                return(

                  <div key={index} className="col-md-6">
                    <div className='table-box' >
                      <p> <b>teacher:</b>  {teacher_Name} </p>
                      <p> <b>student:</b>  {student_Name} </p>
                      <p> <b>class:</b>  {class_Name} </p>
                      <p> <b>class state</b>  <span>nun</span> </p>
                      <p> <b>class information:</b>  Lorem ipsum dolor sit amet consectetur adipisicing elit. Eveniet error impedit iusto nihil magni facilis veniam dolores mollitia nesciunt ipsam, temporibus, architecto suscipit dolore est natus, et reiciendis corrupti tenetur!</p>
                      <p> <b>date:</b> <span>{`${year} ${month} ${day} ${hour}:${minute}`} </span></p>
                      <div className='btn-div'>
                        <button >
                          confirm class
                        </button>
                        <button >
                          gancel class
                        </button>
                      </div>
                    </div>
                  </div>
                )
              })
            }
          </div>
        </div>
      </div>
    </>
  )
}

export default Teacher
