import {React, useState,useEffect} from 'react'
import {useDispatch } from 'react-redux';
import {createUser, updateUser, deleteBlog} from '../../reducer/action'
import { useSelector } from 'react-redux';
import BlogUpdate from './blogUpdate';

import Input from './Input';
const Admin = (props) =>{
  let state = useSelector((state) => state.allData)
  const [currentId, setCurrentId] = useState(0)
  const [deleteId, setDeleteId] = useState(0)
  const [count, setCount] = useState(0)
  const [path, setPath] = useState("")
  const [slideimg, setSlideimg] = useState("")
  const [objectUrl, setObjectUrl] = useState("")
  console.log(slideimg)
  const dispatch = useDispatch()
  const [userData, setuserData] =  useState({ 
    title: '', 
    text: '', 
    name: '', 
    file: [],
  })


  const handleChangetext = (e) => {
    setuserData({...userData, [e.target.name]: e.target.value})
  }

  const update = useSelector((state) =>(
    currentId ? state.allData.find((blog) => blog._id === currentId) : null
  ))

  const handleChangefile = (e) => {
    setuserData({...userData, file:  [ ...userData.file, e.target.files[0] ],})

    const reader = new FileReader();

    // reader.onload = () =>{
    //   if(reader.readyState === 2){
    //     setObjectUrl(reader.result)
    //   }
    // }
    // reader.readAsDataURL(e.target.files[0])
  }
  // console.log(objectUrl)
  useEffect(() => {
    if(update){
      setuserData(update)
    }
  }, [update])

  function handleUpdateimg(filepath) {

    const itemToRemoveIndex = userData.file.findIndex( function(item) {
      return item.filepath === filepath
    })
    setCount(count + 1)
    userData.file.splice(itemToRemoveIndex, 1)
    return 
  }


  let Itemindex  = false
   Itemindex = userData.file.filter((img) => img.name === slideimg)
  console.log(Itemindex)

  const handlerSubmit = (e) =>{
    e.preventDefault();

    if(slideimg){
      if(slideimg !== userData.file[0].name){
        userData.file.splice(0,0, Itemindex[0] )
        userData.file.pop()
      } 
    }
    console.log(userData.file)


    if(currentId === 0){
      if(userData.title && userData.text && userData.name && userData.file){
        userData.blogName = userData.name.replace(/[^a-zA-Z ]/g, "")
        dispatch(createUser(userData));
        setuserData({
          title: '', 
          text: '', 
          blogName: '', 
          file: []
        })
      }else{
        alert("please insert data")
      }
    }else{

      dispatch(updateUser(currentId, userData))

      setCurrentId(0)


      setuserData({
        title: '', 
        text: '', 
        blogName: '', 
        file: []
      })
    }

  }
  // console.log(state)

  useEffect(() => {
    if(deleteId){
      dispatch(deleteBlog(deleteId))
      // console.log(deleteId)
    }
    console.log()

  }, [deleteId,dispatch])

  return(
    <article >
      <div className="container">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12">
              <form autoComplete="off" className='admin-form' action="/upload" method='POST'  >
              <label htmlFor="name">name
                <input required type='text' name="name" variant="outlined"  id='name' 
                  onChange={handleChangetext}
                ></input> 
              </label>
              <label htmlFor="title">title
              <input required type='text' name="title" variant="outlined" id='title' 
                onChange={handleChangetext}
              ></input> 

              </label>
              <label required htmlFor="text">text
                <textarea  name="text" variant="outlined" id='text' 
                  onChange={handleChangetext}
                ></textarea> 
              </label> 
              {
                update ? (
                  <>
                   {
                     userData.file.length == 1 ?(
                       <>
                        <Input handleChangefile={handleChangefile} />
                       </>

                     ):(
                      <>
                      {
                        userData.file.length == 0 ?(
                          <>
                            <Input handleChangefile={handleChangefile} />
                            <Input handleChangefile={handleChangefile} />
                          </>
                        ):(
                          <>
                            {
                          userData.file ? (
                          <>
                            <select id="file" onChange={(e) => setSlideimg(e.target.value)} >
                              <option select="selected"> set slide </option>
                              {
                                userData.file.map((file, index) =>{
                                  // console.log(file)
                                  const {name} = file
                                  return(

                                    <option key={index} value={name} > {name} </option>
                                  )
                                })
                              }
                            </select>
                              </>
                              ):(
                                null
                              )
                            }
                          </>
                        )
                      }
                      </>
                     )
                   }
                  </>
                ):(
                  <>
                    <Input handleChangefile={handleChangefile} />
                    <Input handleChangefile={handleChangefile} />
                    {
                      userData ? (
                      <>
                        <select id="file" onChange={(e) => setSlideimg(e.target.value)} >
                          <option select="selected"> set slide </option>
                          {
                            userData.file.map((file, index) =>{
                              // console.log(file)
                              const {name} = file
                              return(

                                <option key={index} value={name} > {name} </option>
                              )
                            })
                          }
                        </select>
                      </>
                      ):(
                        null
                      )
                    }
                  </>
                )
              }
                <button type="submit" className="btn btn-primary" onClick={handlerSubmit} >
                  {
                    update ? (
                      <p>Update</p>
                    ):(
                      <p>Create</p>
                    )
                  }
                </button>
              </form> 
            </div>
          </div>
        </div>
      </div>

        <div className="container">
          <div className="container-fluid">
            <div className="row">
              {
                update ? (
                <BlogUpdate 
                  userData={userData}  
                  currentId={currentId} 
                  state={state} 
                  update={update}
                  setCurrentId={setCurrentId}
                  handleUpdateimg={handleUpdateimg}
                  setDeleteId={setDeleteId}
                  objectUrl={objectUrl}       
                />
                ):(
                  state.map((blog) =>{
                    const {_id, blogName, file, text,title} = blog

                    return(
                      <div key={_id} className='col-md-4'>
                        {
                          file.map((image, index)=>{
                            const {filepath} = image
                            // console.log(image)
                            return(
                              <div key={index}>
                                <div>
                                  <img className="card-img-top" 
                                  src={`http://localhost:5000/${filepath}`} 
                                  alt="img" />
                                  </div>
                              </div>
                            )
                          })
                        }

                          <div className="card">
                            <div className="card-body">
                              <h5 className="card-title">title: {title} </h5>
                              <h6 className="card-subtitle mb-2 text-muted">blogName: {blogName}</h6>
                              <p className="card-text">text: {text}</p>
                              <button
                              className="btn btn-success btn-sm"
                              onClick={()=> setCurrentId(_id)}
                              >
                              update
                              </button>
                              <button
                                className="btn  btn-danger btn-sm"
                                onClick={()=> setDeleteId(_id)} >
                                delete
                              </button>
                            </div>
                          </div>

                      </div>
                    )
                  })
                )
              }
            </div>
          </div>
        </div>

    </article>
  )

}

export default Admin
