import React,{useState,useRef,useEffect} from 'react'
import img1 from './metaImg/image1.png';
import img2 from './metaImg/image2.png';
import img3 from './metaImg/image3.png';
import {useSpring,animated} from 'react-spring';
import { MetateskFunction } from '../functions/metateskFunctions';

const SectionTwo = () => {
  const [Scroll, getScroll] = useState('')  



  const [opacity, setOpacity] = useState(0);
  const myref = useRef(null);
  // cons
  useEffect(() => {
    if(myref.current){
      getScroll(myref.current.offsetTop)
      // 
    }
  }, [])


    const [scrollPosition, setScrollPosition] = useState(0);
    const handleScroll = () => {
        const position = window.pageYOffset;
        setScrollPosition(position);
        // console.log(Scroll)
    };

    console.log(Scroll < scrollPosition)

  window.addEventListener('scroll', handleScroll)





  const imgAnimation = useSpring({
		to: [{opacity: 1 }],
		from: {opacity: 0  },
    config: {
      duration: 2000
    }
	});


  return (
    <article className='section-Two' ref={myref}>
      <div className="container">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-4 ">
              <animated.div  className="metatesk-box " >
                <div className="img-div">
                  <img src={img1} alt="" />
                </div>
                <p>Anytime, Anywhere join the classes</p>
              </animated.div>


            </div>

            <div className="col-md-4 ">
              <div className="metatesk-box " >
                <div className="img-div">
                  <img src={img2} alt="" />
                </div>

                <p>Interact
                    with other users 
                    in virtual world</p>
              </div>
            </div>

            <div className="col-md-4 ">
              <div className="metatesk-box " >
                <div className="img-div">
                  <img src={img3} alt="" />
                </div>
                <p>Entertain 
                  with gamified 
                  engaging classes </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </article>
  )
}

export default SectionTwo
