import React, {useState,useEffect} from 'react'
import { useSelector } from 'react-redux';
import {GetTimetable} from '../../../../reducer/crmRedux/action'
import { useHistory } from 'react-router-dom';
import {useDispatch } from 'react-redux';


const Create = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(GetTimetable())
  }, [])

  let state = useSelector((state) => state);
  console.log(state)
  const [newTimetable, setNewtimetable] = useState({
    classesId:'',
    classesName:'',
    studentsId:'',
    studentsName:'',
    teacherId:'',
    teacherName:'',
    date:''
  })


  state.classReducer.map((Class) =>{
    const {name, _id} = Class
    if(_id === newTimetable.classesId){
        newTimetable.classesName = name
      console.log(newTimetable)
    }
  })

  state.studentReducer.map((student) =>{
    const {firstName, _id} = student
    if(_id === newTimetable.studentsId){
      newTimetable.studentsName = firstName
    console.log(newTimetable)
    }
  })

  state.teacherReducer.map((student) =>{
    const {name, _id} = student
    if(_id === newTimetable.teacherId){
      newTimetable.teacherName = name
    console.log(newTimetable)
    }
  })

  const history = useHistory();
  // const dispatchAction = (e) =>{
  //   e.preventDefault()
  //   console.log(newTimetable)
  //   dispatch(CreateTimetable(newTimetable, history))
  // }


  return (
    <div className="container">
      <div className="container-fluid">
        <div className="row">

          <div className="col-md-5">
            <div className="create-div">
              <form action="">
                {/* select class */}
                <select
                  onChange={(e) =>{
                    setNewtimetable({
                      ...newTimetable, classesId: e.target.value
                    })
                  }} 
                  name="class" id="cars">
                  <option  value="select">select</option>
                  {
                    state.classReducer.map((clas, index) =>{
                      const {name, _id} = clas

                      return(
                        <option key={index} value={_id}>{name}</option>
                      )
                    })
                  }
                </select>
                  {/* select student */}
                <select
                onChange={(e) =>{
                  setNewtimetable({
                    ...newTimetable, studentsId: e.target.value
                  })
                }}
                name="students" id="cars">
                  <option  value="select">select</option>
                  {
                    state.studentReducer.map((student, index) =>{
                      const {firstName, _id} = student

                      return(
                        <option key={index} value={_id}>{firstName}</option>
                      )
                    })
                  }
                </select>
                {/* select teacher  */}
                <select 
                  onChange={(e) =>{
                    setNewtimetable({
                      ...newTimetable, teacherId: e.target.value
                    })
                  }}
                name="students" id="cars">
                  <option  value="select">select</option>
                  {
                    state.teacherReducer.map((teacher, index) =>{
                      const {name, _id} = teacher

                      return(
                        <option key={index} value={_id}>{name}</option>
                      )
                    })
                  }
                </select>
                <ul>
                  {
                    state.timetableReducer.map((timetable) =>{
                      const {class_Name, student_Name,teacher_Name} = timetable

                      return(
                        <li>
                          {class_Name}
                          {student_Name}
                          {teacher_Name}  
                        </li>
                      )
                    })
                  }
                </ul>
                {/*  */}
                {/* <button
                onClick={() =>{
                  dispatchAction()
                }}
                >
                  create
                </button> */}
              </form>
            </div>
          </div>

        </div>
      </div>
    </div>

  )
}

export default Create
