import React from 'react'

function BlogUpdate({
  userData,
  currentId,
  update,
  setCurrentId,
  handleUpdateimg,
  setDeleteId
  }) {
    const {_id, blogName, file, text,title} = userData

  return (  
    <div>
      {
        userData.title ? console.log(true) : console.log(false)
      }
      {  
      userData._id ? (
        <>
          <div className="col-md-12" key={_id}>
            <div className="updateBlog" >
              <div className="updateimgDiv">
                {
                  userData.file.map((image, index)=>{
                    const {filepath} = image
                    // console.log(image)
                    return(

                      <div  key={index}>
                        {
                          <div className='updateImage'>
                            { 
                              currentId === _id ?
                              (<button onClick={() =>handleUpdateimg(filepath)}>remove</button>) :
                              (null)
                            }
                              <img className="" 
                              src={`http://localhost:5000/${filepath}`} 
                              alt="img"
                              />
                            </div>
                        }
                      </div>
                    )
                  })
                }
              </div>
              <div className="card-body">
                <h5 className="card-title">title: {title} </h5>
                <h6 className="card-subtitle mb-2 text-muted">blogName {blogName}</h6>
                <p className="card-text">text: {text}</p>

                <button
                    className="btn btn-success btn-sm"
                    onClick={()=> setCurrentId(_id)}
                  >
                    update
                  </button>
                <button
                  className="btn  btn-danger btn-sm"
                  onClick={()=> setDeleteId(_id)} >
                  delete
                </button>
              </div>
            </div>
          </div>
        </>
      ):(
        <b>Wait</b>
      )


        }
    </div>
  )
}

export default BlogUpdate
